package Free_Coding_Camp;

public class Var_data_type {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//primitive data type
		int hello_world=5; //valid
		double num2=5.0;
		boolean b=true;
		char c='p';
		//Not primitive
		String str="Parag";
		//int  hello name; invalid
		//int hello*; invalid
		//int _hello_; //valid
		int par=hello_world;
		System.out.println(hello_world);
		System.out.println(num2);
		System.out.println(b);
		System.out.println(c);
		System.out.println(str);
		System.out.println(par);
		
		//int hello_world=5; this will throw error as var is declarated after it is called
		
		
	}

}
